import React, { Component } from 'react';

class Main extends Component {

  render() {
    return (
      <div className="container-fluid text-monospace">
      <br></br>
      &nbsp;
      <br></br>
        <div className="row">
          <div className="col-md-10">
            <div className="embed-responsive embed-responsive-16by9" style={{ maxHeight: '768px'}}>
              <video src={`https://ipfs.infura.io/ipfs/${this.props.currentHash}`} controls ></video>
            </div>
            <h3>{/* Code... */}</h3>
          </div>
          <div className="col-md-2 overflow-auto text-center" style={{ maxHeight: '768px', minWidth: '175px' }}>
            <h5><b>Share Video</b></h5>
            <form onSubmit={(event) => {
              //upload video
              event.preventDefault()
              const title = this.videoTitle.value 
              this.props.uploadVideo(title)
            }} >
              &nbsp;
              <input type="file" accept=".mp4, .mkv .ogg .wmv" onChange={this.props.captureFile} style={{width: '250px'}}/>
              <div className="form-group mr-sm-2">
                <input
                  id="videoTitle" 
                  type="text"
                  className="form-control-sm"
                  placeholder="Title..."
                  ref={(input)=>{this.videoTitle=input}}
                  required
                  />
              </div>
              <button type="submit" className="btn btn-danger btn-black btn-sm">Upload</button>
              &nbsp;
            </form>
            {/* Map Video...*/}
              {/* Return Video...*/}
              <div style={{ width: '175px'}}>
                <div className="card-title bg-dark">
                  <small className="text-white"><b>{/*Video title*/}</b></small>
                </div>
                  <div>
                    {/* Change Video...*/}
                    {/* Return Side Videos...*/}
                  </div>
              </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Main;
